import React, { useState } from 'react';
import axios from 'axios';
import SearchInput from './components/SearchInput';
import PostList from './components/PostList';
import { rankPosts } from './utils/ranking';

function App() {
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentQuery, setCurrentQuery] = useState('');

  // SerpAPI configuration
  const SERPAPI_KEY = process.env.REACT_APP_SERPAPI_KEY || 'your_serpapi_key_here';
  const SERPAPI_URL = 'https://serpapi.com/search.json';

  const searchPosts = async (query) => {
    setIsLoading(true);
    setError(null);
    setCurrentQuery(query);

    try {
      // If no API key is provided, use mock data for demonstration
      if (SERPAPI_KEY === 'your_serpapi_key_here') {
        console.warn('Using mock data. Please set REACT_APP_SERPAPI_KEY environment variable for real results.');
        await simulateAPICall();
        const mockResults = generateMockResults(query);
        const rankedResults = rankPosts(query, mockResults);
        setPosts(rankedResults);
      } else {
        // Make actual SerpAPI call
        const response = await axios.get(SERPAPI_URL, {
          params: {
            api_key: SERPAPI_KEY,
            engine: 'google',
            q: query,
            num: 20, // Get more results for better ranking
            gl: 'us',
            hl: 'en'
          }
        });

        if (response.data.organic_results) {
          const results = response.data.organic_results.map(result => ({
            title: result.title || '',
            link: result.link || '',
            snippet: result.snippet || ''
          }));

          const rankedResults = rankPosts(query, results);
          setPosts(rankedResults);
        } else {
          throw new Error('No results found');
        }
      }
    } catch (err) {
      console.error('Search error:', err);
      setError(err.message || 'Failed to fetch results. Please try again.');
      setPosts([]);
    } finally {
      setIsLoading(false);
    }
  };

  // Simulate API call delay for demo
  const simulateAPICall = () => {
    return new Promise(resolve => setTimeout(resolve, 1500));
  };

  // Generate mock results for demonstration
  const generateMockResults = (query) => {
    const mockResults = [
      {
        title: `Complete Guide to ${query} - Best Practices and Tips`,
        link: 'https://example.com/guide-to-' + query.toLowerCase().replace(/\s+/g, '-'),
        snippet: `Learn everything about ${query} with this comprehensive guide. Covers fundamentals, advanced techniques, and real-world applications. Perfect for beginners and experts alike.`
      },
      {
        title: `${query} Tutorial: Step-by-Step Instructions`,
        link: 'https://tutorial-site.com/' + query.toLowerCase().replace(/\s+/g, '-'),
        snippet: `Follow our detailed tutorial on ${query}. Includes code examples, screenshots, and practical exercises to help you master the concepts quickly.`
      },
      {
        title: `Top 10 ${query} Tools and Resources`,
        link: 'https://tools-review.com/top-' + query.toLowerCase().replace(/\s+/g, '-') + '-tools',
        snippet: `Discover the best tools and resources for ${query}. We've tested and reviewed the top options to help you choose the right solution for your needs.`
      },
      {
        title: `${query} vs Alternatives: Comprehensive Comparison`,
        link: 'https://comparison-site.com/' + query.toLowerCase().replace(/\s+/g, '-') + '-comparison',
        snippet: `Compare ${query} with other popular alternatives. Detailed analysis of features, pricing, pros and cons to help you make an informed decision.`
      },
      {
        title: `Advanced ${query} Techniques for Professionals`,
        link: 'https://advanced-tech.com/' + query.toLowerCase().replace(/\s+/g, '-') + '-advanced',
        snippet: `Take your ${query} skills to the next level with these advanced techniques. Expert tips and strategies used by industry professionals.`
      },
      {
        title: `${query} Case Studies and Success Stories`,
        link: 'https://case-studies.com/' + query.toLowerCase().replace(/\s+/g, '-') + '-cases',
        snippet: `Real-world case studies showing how companies successfully implemented ${query}. Learn from their experiences and avoid common pitfalls.`
      },
      {
        title: `Free ${query} Course - Learn from Scratch`,
        link: 'https://free-courses.com/' + query.toLowerCase().replace(/\s+/g, '-') + '-course',
        snippet: `Start learning ${query} today with our free comprehensive course. Video lessons, quizzes, and hands-on projects included.`
      },
      {
        title: `${query} Best Practices and Common Mistakes`,
        link: 'https://best-practices.com/' + query.toLowerCase().replace(/\s+/g, '-') + '-practices',
        snippet: `Avoid common mistakes and follow industry best practices for ${query}. Tips from experienced practitioners and thought leaders.`
      },
      {
        title: `Getting Started with ${query} - Beginner's Guide`,
        link: 'https://beginners-guide.com/getting-started-' + query.toLowerCase().replace(/\s+/g, '-'),
        snippet: `New to ${query}? This beginner-friendly guide will help you understand the basics and get started with your first project.`
      },
      {
        title: `${query} Community Forum and Discussion`,
        link: 'https://community-forum.com/' + query.toLowerCase().replace(/\s+/g, '-') + '-forum',
        snippet: `Join the ${query} community! Ask questions, share experiences, and connect with other enthusiasts and professionals.`
      }
    ];

    return mockResults;
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <header className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            🎯 Post Recommendation System
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Find and rank relevant posts using advanced keyword matching and TF-IDF similarity algorithms
          </p>
        </header>

        {/* Search Input */}
        <SearchInput onSearch={searchPosts} isLoading={isLoading} />

        {/* API Key Notice */}
        {SERPAPI_KEY === 'your_serpapi_key_here' && (
          <div className="max-w-2xl mx-auto mb-8 bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-start gap-3">
              <span className="text-yellow-600 text-xl">⚠️</span>
              <div>
                <h3 className="font-semibold text-yellow-800 mb-1">Demo Mode</h3>
                <p className="text-sm text-yellow-700">
                  Currently using mock data for demonstration. To use real search results, 
                  set your <code className="bg-yellow-100 px-1 rounded">REACT_APP_SERPAPI_KEY</code> environment variable.
                </p>
                <p className="text-xs text-yellow-600 mt-1">
                  Get your free API key at <a href="https://serpapi.com" target="_blank" rel="noopener noreferrer" className="underline">serpapi.com</a>
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Error Message */}
        {error && (
          <div className="max-w-2xl mx-auto mb-8 bg-red-50 border border-red-200 rounded-lg p-4">
            <div className="flex items-center gap-3">
              <span className="text-red-600 text-xl">❌</span>
              <div>
                <h3 className="font-semibold text-red-800">Error</h3>
                <p className="text-red-700">{error}</p>
              </div>
            </div>
          </div>
        )}

        {/* Results */}
        {posts.length > 0 && (
          <PostList posts={posts} query={currentQuery} />
        )}

        {/* No Results */}
        {!isLoading && !error && posts.length === 0 && currentQuery && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">🔍</div>
            <h3 className="text-xl font-semibold text-gray-700 mb-2">No results found</h3>
            <p className="text-gray-500">Try a different search query</p>
          </div>
        )}

        {/* Footer */}
        <footer className="mt-16 text-center text-gray-500 text-sm">
          <p>Built with React • Powered by keyword matching + TF-IDF similarity</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
