import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import SearchInput from './components/SearchInput';
import PostList from './components/PostList';
import ScrapedContentPage from './pages/ScrapedContentPage';
import FormattedContentPage from './pages/FormattedContentPage';
import { rankPosts } from './utils/ranking';

function HomePage() {
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentQuery, setCurrentQuery] = useState('');
  const [searchHistory, setSearchHistory] = useState([]);
  const [isLoadingHistory, setIsLoadingHistory] = useState(false);

  // Backend API configuration
  const BACKEND_URL = 'http://localhost:5000/api/search';
  const HISTORY_URL = 'http://localhost:5000/api/search-history';

  const searchPosts = async (query) => {
    setIsLoading(true);
    setError(null);
    setCurrentQuery(query);

    try {
      // Make API call to our backend proxy
      const response = await axios.get(BACKEND_URL, {
        params: {
          q: query
        }
      });

      if (response.data.organic_results) {
        const results = response.data.organic_results.map(result => ({
          title: result.title || '',
          link: result.link || '',
          snippet: result.snippet || ''
        }));

        const rankedResults = rankPosts(query, results);
        setPosts(rankedResults);
      } else {
        throw new Error('No results found');
      }
    } catch (err) {
      console.error('Search error:', err);
      setError(err.message || 'Failed to fetch results. Please try again.');
      setPosts([]);
    } finally {
      setIsLoading(false);
    }
  };

  const loadSearchHistory = async () => {
    setIsLoadingHistory(true);
    try {
      const response = await axios.get(HISTORY_URL);
      if (response.data.success) {
        setSearchHistory(response.data.history);
      }
    } catch (err) {
      console.error('Error loading search history:', err);
    } finally {
      setIsLoadingHistory(false);
    }
  };

  // Load search history on component mount
  React.useEffect(() => {
    loadSearchHistory();
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <header className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            🎯 Post Recommendation System
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Find and rank relevant posts using advanced keyword matching and TF-IDF similarity algorithms
          </p>
        </header>

        {/* Search Input */}
        <SearchInput onSearch={searchPosts} isLoading={isLoading} />

        {/* Error Message */}
        {error && (
          <div className="max-w-2xl mx-auto mb-8 bg-red-50 border border-red-200 rounded-lg p-4">
            <div className="flex items-center gap-3">
              <span className="text-red-600 text-xl">❌</span>
              <div>
                <h3 className="font-semibold text-red-800">Error</h3>
                <p className="text-red-700">{error}</p>
              </div>
            </div>
          </div>
        )}

        {/* Results */}
        {posts.length > 0 && (
          <PostList posts={posts} query={currentQuery} />
        )}

        {/* No Results */}
        {!isLoading && !error && posts.length === 0 && currentQuery && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">🔍</div>
            <h3 className="text-xl font-semibold text-gray-700 mb-2">No results found</h3>
            <p className="text-gray-500">Try a different search query</p>
          </div>
        )}

        {/* Search History Section */}
        {searchHistory.length > 0 && (
          <div className="mt-16">
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-2">
                📚 Search History
              </h2>
              
              {isLoadingHistory ? (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
                  <p className="text-gray-600">Loading history...</p>
                </div>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {searchHistory.map((search, index) => (
                    <div key={index} className="bg-gray-50 rounded-lg p-4 border border-gray-200 hover:shadow-md transition-shadow">
                      <div className="flex items-start justify-between mb-3">
                        <h3 className="font-semibold text-gray-800 text-sm">
                          🔍 "{search.query}"
                        </h3>
                        <span className="text-xs text-gray-500">
                          {new Date(search.timestamp).toLocaleDateString()}
                        </span>
                      </div>
                      
                      <div className="text-xs text-gray-600 mb-3">
                        📊 {search.results?.length || 0} results • {search.totalResults?.toLocaleString() || 0} total
                      </div>
                      
                      {search.results && search.results.length > 0 && (
                        <div className="space-y-2">
                          {search.results.slice(0, 2).map((result, resultIndex) => (
                            <div key={resultIndex} className="bg-white rounded p-2 border">
                              <h4 className="text-xs font-medium text-gray-800 mb-1 line-clamp-1">
                                {result.title}
                              </h4>
                              <p className="text-xs text-gray-600 line-clamp-2">
                                {result.snippet}
                              </p>
                              <a 
                                href={result.link}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="text-xs text-blue-600 hover:text-blue-800 hover:underline mt-1 inline-block"
                              >
                                View Original →
                              </a>
                            </div>
                          ))}
                          
                          {search.results.length > 2 && (
                            <div className="text-xs text-gray-500 text-center py-1">
                              +{search.results.length - 2} more results
                            </div>
                          )}
                        </div>
                      )}
                      
                      <button
                        onClick={() => {
                          setCurrentQuery(search.query);
                          searchPosts(search.query);
                        }}
                        className="w-full mt-3 bg-blue-600 hover:bg-blue-700 text-white text-xs py-2 px-3 rounded transition-colors"
                      >
                        🔄 Search Again
                      </button>
                    </div>
                  ))}
                </div>
              )}
              
              <div className="mt-6 text-center">
                <button
                  onClick={loadSearchHistory}
                  className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                >
                  🔄 Refresh History
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Footer */}
        <footer className="mt-16 text-center text-gray-500 text-sm">
          <p>Built with React • Powered by keyword matching + TF-IDF similarity • Search history saved locally</p>
        </footer>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/scrape/:encodedUrl" element={<ScrapedContentPage />} />
        <Route path="/formatted" element={<FormattedContentPage />} />
      </Routes>
    </Router>
  );
}

export default App;
