import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import axios from 'axios';

const FormattedContentPage = () => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const [formattedContent, setFormattedContent] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [originalData, setOriginalData] = useState(null);

  // Get the URL, title, and content from query parameters
  const url = searchParams.get('url') || '';
  const title = searchParams.get('title') || 'Formatted Content';
  const content = searchParams.get('content') || '';

  useEffect(() => {
    const formatContent = async () => {
      if (!content) {
        setError('No content available to format');
        setIsLoading(false);
        return;
      }

      setIsLoading(true);
      setError(null);

      try {
        console.log('Formatting content with Gemini AI...');
        const response = await axios.post('http://localhost:5000/api/format-content', {
          content: decodeURIComponent(content),
          title: title
        }, {
          timeout: 60000 // 60 seconds timeout
        });

        if (response.data.success) {
          setFormattedContent(response.data.formattedContent);
          setOriginalData(response.data.originalContent);
        } else {
          throw new Error('Failed to format content');
        }
      } catch (error) {
        console.error('Content formatting error:', error);
        
        let errorMessage = 'Failed to format content';
        if (error.code === 'ECONNREFUSED') {
          errorMessage = 'Server is not running. Please start the backend server.';
        } else if (error.response?.data?.error) {
          errorMessage = error.response.data.error;
        } else if (error.message) {
          errorMessage = error.message;
        }
        
        setError(errorMessage);
      } finally {
        setIsLoading(false);
      }
    };

    formatContent();
  }, [content, title]);

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  const copyAllContent = () => {
    const allContent = formattedContent.map((section, index) => 
      `${index + 1}. ${section.headline}\n${section.content}`
    ).join('\n\n');
    copyToClipboard(allContent);
  };

  const shareOnTwitter = (section, index) => {
    const tweetText = encodeURIComponent(`${section.headline}\n\n${section.content}`);
    window.open(`https://twitter.com/intent/tweet?text=${tweetText}`, '_blank');
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg">
        <div className="max-w-4xl mx-auto px-4 py-6">
          <div className="flex items-center justify-between">
            <div>
              <button
                onClick={() => navigate(-1)}
                className="flex items-center gap-2 text-blue-100 hover:text-white mb-3 transition-colors"
              >
                ← Back to Content
              </button>
              <h1 className="text-3xl font-bold flex items-center gap-3">
                ✨ AI-Enhanced Content
              </h1>
              <p className="text-blue-100 mt-2">{title}</p>
            </div>
            {formattedContent.length > 0 && (
              <button
                onClick={copyAllContent}
                className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white px-6 py-3 rounded-lg font-semibold transition-all duration-200 transform hover:scale-105 flex items-center gap-2"
              >
                📋 Copy All
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="max-w-4xl mx-auto px-4 py-8">
        {isLoading && (
          <div className="text-center py-20">
            <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-blue-600 mx-auto mb-6"></div>
            <h2 className="text-2xl font-semibold text-gray-700 mb-2">Enhancing Content with AI...</h2>
            <p className="text-gray-600">This may take a few moments</p>
          </div>
        )}

        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-6 mb-8">
            <div className="flex items-center gap-4">
              <span className="text-red-600 text-3xl">❌</span>
              <div>
                <h3 className="text-xl font-semibold text-red-800">Content Formatting Error</h3>
                <p className="text-red-700 mt-1">{error}</p>
                <button
                  onClick={() => navigate(-1)}
                  className="mt-3 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
                >
                  Go Back
                </button>
              </div>
            </div>
          </div>
        )}

        {formattedContent.length > 0 && (
          <div>
            {/* Content Stats */}
            <div className="flex flex-wrap gap-3 mb-6 p-4 bg-white rounded-lg shadow-sm border">
              <div className="flex items-center gap-2 text-sm text-gray-600">
                📊 {formattedContent.length} sections generated
              </div>
              <div className="flex items-center gap-2 text-sm text-gray-600">
                📄 {originalData?.contentLength?.toLocaleString()} characters processed
              </div>
              <div className="flex items-center gap-2 text-sm text-gray-600">
                🤖 Enhanced by Alex Hormozi AI
              </div>
            </div>

            {/* Formatted Content Sections */}
            <div className="space-y-4">
              {formattedContent.map((section, index) => (
                <div key={index} className="bg-white border border-gray-200 rounded-lg shadow-sm hover:shadow-md transition-shadow">
                  <div className="p-6">
                    <div className="flex items-start justify-between gap-4">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-3">
                          <span className="bg-blue-100 text-blue-800 text-xs font-semibold px-2 py-1 rounded-full">
                            {index + 1}/{formattedContent.length}
                          </span>
                          <span className="text-xs text-gray-500">
                            Section {index + 1}
                          </span>
                        </div>
                        
                        {/* Headline */}
                        <h3 className="text-xl font-bold text-gray-900 mb-3 leading-tight">
                          {section.headline}
                        </h3>
                        
                        {/* Content */}
                        <p className="text-gray-700 leading-relaxed text-base">
                          {section.content}
                        </p>
                      </div>
                      
                      {/* Action Buttons */}
                      <div className="flex flex-col gap-2">
                        <button
                          onClick={() => copyToClipboard(`${section.headline}\n\n${section.content}`)}
                          className="text-gray-500 hover:text-blue-600 p-2 rounded transition-colors"
                          title="Copy section"
                        >
                          📋
                        </button>
                        <button
                          onClick={() => shareOnTwitter(section, index)}
                          className="text-gray-500 hover:text-blue-600 p-2 rounded transition-colors"
                          title="Share on Twitter"
                        >
                          🐦
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Tips Section */}
            <div className="mt-8 p-6 bg-gradient-to-r from-yellow-50 to-orange-50 border border-yellow-200 rounded-lg">
              <h4 className="font-bold text-yellow-800 mb-3 flex items-center gap-2">
                💡 Content Enhancement Tips
              </h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-yellow-700">
                <div>
                  <p className="font-medium mb-1">📈 Business Focus</p>
                  <p>Content enhanced with actionable business insights and practical advice</p>
                </div>
                <div>
                  <p className="font-medium mb-1">🎯 No-Bullshit Approach</p>
                  <p>Straight-forward, honest advice without sugarcoating</p>
                </div>
                <div>
                  <p className="font-medium mb-1">⚡ Quick Implementation</p>
                  <p>Each section provides immediately actionable takeaways</p>
                </div>
                <div>
                  <p className="font-medium mb-1">🚀 Entrepreneur Mindset</p>
                  <p>Formatted with Alex Hormozi's proven business philosophy</p>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Footer */}
      <footer className="bg-gray-800 text-white py-8 mt-16">
        <div className="max-w-4xl mx-auto px-4 text-center">
          <p className="text-gray-300">
            Content enhanced by Alex Hormozi AI • Powered by Gemini •
            <button
              onClick={() => navigate(-1)}
              className="text-blue-400 hover:text-blue-300 ml-2 underline"
            >
              Back to Original Content
            </button>
          </p>
        </div>
      </footer>
    </div>
  );
};

export default FormattedContentPage;
